/** * This is a code snippet for the ini config-file handling. */#include "iniparser.h" // extern library (http://ndevilla.free.fr/iniparser/)#define FILE_NAME "example.ini"	// name of the *.ini config file/*---------------------------------------------------------------------------  							Function prototypes ---------------------------------------------------------------------------*/int parse_ini_file(char * ini_name);/** * represents a bricklet * to consume information out of the .ini config file */typedef struct _bricklet_ {	char* name;	char** keyValue;} bricklet;/** * main function for testing purpose * */int main() {	char *fileName = FILE_NAME;	parse_ini_file(fileName);	return (0);}/** * parser function */int parse_ini_file(char * ini_name) {	dictionary * configFile;	// read in the config file	configFile = iniparser_load(ini_name);	if (configFile == NULL) {		fprintf(stderr, "cannot parse file [%s]", ini_name);		return (-1);	}	// TODO : create dyn. array of type bricklet	short index = 0;	char* value;	while (configFile->key[index] != '\0') {		value = iniparser_getstr(configFile, configFile->key[index]);		if (value == NULL) {			// TODO: create new bricklet in the bricklet_array			printf("section: %s\n", configFile->key[index]);			index++;			continue;		}		printf("KEY:%s VALUE:%s \n", configFile->key[index], value);		index++;	}	iniparser_freedict(configFile);	return (0); // TODO : return the bricklet_array}